import React, {Component} from 'react';
import {DataSearch, ReactiveBase, ReactiveList, ResultList, SelectedFilters, /*MultiList,*/ SingleRange, RangeSlider, MultiDataList, DateRange} from '@appbaseio/reactivesearch';
import './SteamSearch.css';

const { ResultListWrapper } = ReactiveList;

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
          isClicked: false,
          message: "ðŸ”¬Show Filters"
        };
      }

      handleClick() {
        this.setState({
          isClicked: !this.state.isClicked,
          message: this.state.isClicked ? "ðŸ”¬ Show Filters" : "ðŸŽ® Show Games"
        });
      }

    render() {

        return (
            <div className="main-container">
                <ReactiveBase
                    app="steam-search"
                    url="http://52.142.54.111:9200"
                    theme={
                        {
                            typography: {
                                fontFamily: 'Arial, Helvetica, sans-serif',
                                fontSize: '16px',
                            },
                            colors: {
                                titleColor: '#c7d5e0',
                                textColor: '#c7d5e0',
                                backgroundColor: '#212121',
                                primaryColor: '#2B475E',
                            }
                        }
                    }
                >
                    <DataSearch
                        componentId="title"
                        dataField={["ResponseName"]}
                        queryFormat="and"
                        placeholder="enter search term"
                        showIcon={false}
                        title="Steam Search"
                        className="data-search"
                        innerClass={{
                            input: 'input',
                            list: 'list',
                        }}
                    />
                    <SelectedFilters/>

                    <div className="sub-container">
                            <div
                                className={
                                this.state.isClicked ? "left-bar-optional" : "left-bar"
                                }   
                            >
                                <div className="filter-heading center">
                                    <b>
                                        {" "}
                                        <i className="fa fa-pied-piper-alt" /> Genres{" "}
                                    </b>
                                </div>

                                <MultiDataList
                                    componentId="genres-list"
                                    const Genres = 
                                    {[
                                        "GenreIsAction", "GenreIsAdventure", "GenreIsCasual", "GenreIsEarlyAccess", "GenreIsFreeToPlay", "GenreIsIndie", "GenreIsMassivelyMultiplayer", "GenreIsNonGame", "GenreIsRacing", "GenreIsRPG", "GenreIsSimulation", "GenreIsSports", "GenreIsStrategy"
                                    ]}
                                    dataField="{Genres"

                                    className="genres-filter"
                                    size={100}
                                    selectAllLabel="All Genres"
                                    placeholder="Search for a genre"     
                                    react={{
                                        and: [
                                            "date-filter",
                                            "RangeSlider",
                                            "price-list",
                                            "resultLists"
                                        ]
                                        }}                               
                                    data={[
                                        {
                                            label: "Action",
                                            value: "GenreIsAction"
                                        },
                                        {
                                            label: "Adventure",
                                            value: "GenreIsAdventure"
                                        },
                                        {
                                            label: "Casual",
                                            value: "GenreIsCasual"
                                        },
                                        {
                                            label: "EarlyAccess",
                                            value: "GenreIsEarlyAccess"
                                        },
                                        {
                                            label: "FreeToPlay",
                                            value: "GenreIsFreeToPlay"
                                        },
                                        {
                                            label: "Indie",
                                            value: "GenreIsIndie"
                                        },
                                        {
                                            label: "MassivelyMultiplayer",
                                            value: "GenreIsMassivelyMultiplayer"
                                        },
                                        {
                                            label: "NonGame",
                                            value: "GenreIsNonGame"
                                        },
                                        {
                                            label: "Racing",
                                            value: "GenreIsRacing"
                                        },
                                        {
                                            label: "RPG",
                                            value: "GenreIsRPG"
                                        },
                                        {
                                            label: "Simulation",
                                            value: "GenreIsSimulation"
                                        },
                                        {
                                            label: "Sports",
                                            value: "GenreIsSports"
                                        },
                                        {
                                            label: "Strategy",
                                            value: "GenreIsStrategy"
                                        },
                                    ]}
                                    showFilter={true}
                                    showCount={true}
                                    URLParams={false}
                                    innerClass={{
                                    label: "list-item",
                                    input: "input"
                                    }}
                                />  

                                <hr className="blue" />                             


                                <div className="filter-heading center">
                                    <b>
                                        {" "}
                                        <i className="fa fa-dollar" /> PriceFinal{" "}
                                    </b>
                                </div>
                                <SingleRange
                                    componentId="price-list"
                                    dataField="PriceFinal"
                                    className="price-filter"
                                    data={[
                                    { start: 0.00, end: 5.00, label: "~ $5" },
                                    { start: 5.00, end: 10.00, label: "$5 - $10" },
                                    { start: 10.00, end: 50.00, label: "$10 - $50" },
                                    { start: 50.00, end: 100.00, label: "$50 - $100" },
                                    { start: 100.00, end: 500.00, label: "$100 ~" }
                                    ]}
                                    showRadio={true}
                                    showFilter={true}
                                    URLParams={false}
                                    innerClass={{
                                    label: "price-label",
                                    radio: "price-radio"
                                    }}
                                />                                
                                <hr className="blue" />

                                <div className="filter-heading center">
                                    <b>
                                        <i className="fa fa-star" /> Ratings
                                    </b>
                                </div>
                                <RangeSlider
                                    componentId="RangeSlider"
                                    dataField="RecommendationCount"
                                    className="range-slider"
                                    range={{
                                    start: 0,
                                    end: 500
                                    }}
                                    rangeLabels={{
                                    start: "0",
                                    end: "500"
                                    }}
                                    react={{
                                    and: [
                                        "date-filter",
                                        "genres-list",
                                        "price-list",
                                        "resultLists",
                                    ]
                                    }}
                                />
                                <hr className="blue" />

                                <div className="filter-heading center">
                                    <b>
                                        {" "}
                                        <i className="fa fa-calendar" /> Release Date{" "}
                                    </b>
                                </div>
                                <DateRange
                                    componentId="date-filter"
                                    dataField="ReleaseDate"
                                    className="datePicker"
                                />
                            </div>

                            <div
                                className={
                                    this.state.isClicked
                                    ? "result-container-optional"
                                    : "result-container"
                                }
                            >



                    <ReactiveList
                        componentId="resultLists"
                        dataField="ResponseName"
                        size={25}
                        pagination={true}
                        react={{
                            "and": [
                                "price-list",
                                "genres-list",
                                "RangeSlider",
                                "date-filter",
                            ]
                        }}
                        sortOptions={[
                                {label: "Best Match", dataField: "RecommendationCount", sortBy: "desc"},
                                {label: "Lowest Price", dataField: "PriceInitial", sortBy: "asc"},
                                {label: "Highest Price", dataField: "PriceInitial", sortBy: "desc"},
                        ]}
                        className="result-list"
                        innerClass={{
                            resultsInfo: "resultsInfo",
                            resultStats: "resultStats",
                        }}
                    >

                        {({data}) => (
                            <ResultListWrapper>
                                {
                                    data.map(item => (
                                        <ResultList key={item._id}
                                                    href={`https://store.steampowered.com/app/${item.ResponseID}`}
                                                    className="listItem"
                                        >
                                            <ResultList.Image className="image" src={item.HeaderImage}/>
                                            <ResultList.Content>
                                                <ResultList.Title
                                                    dangerouslySetInnerHTML={{
                                                        __html: item.ResponseName
                                                    }}
                                                />
                                                <ResultList.Description>
                                                  {/* <p className="headerImage">${item.HeaderImage}</p> */}
                                                  <p className="releaseDate">{item.ReleaseDate}</p>
                                                  <p className="price">${item.PriceInitial}</p>
                                                </ResultList.Description>
                                            </ResultList.Content>
                                        </ResultList>
                                    ))
                                }
                            </ResultListWrapper>
                        )}
                    </ReactiveList>
                    </div>
                    <button
                        className="toggle-button"
                        onClick={this.handleClick.bind(this)}
                        >
                        {this.state.message}
                    </button>
                    </div>
                </ReactiveBase>
            </div>
        );
    }
}

export default App;
